workflow-item.script:
"use strict"

if(inputProperties.customProperties.ad_OU == undefined || inputProperties.customProperties.ad_OU == null || inputProperties.customProperties.ad_OU.length == 0) {
    System.warn("No ad_OU property, nothing to do.");
} else {
    try {
      changeOU(inputProperties);
    } catch(exception) {
        System.error("Unable to change the OU for VM '" + inputProperties.resourceNames[0] + "': " + exception);
        try {
            sendFailureNotification(inputProperties, exception);
        } catch(exception2) {
            System.error("Unable to send email: " + exception2)
        }
    }
}

/**
 * 
 */ 
function changeOU(inputProperties) { 
    var adHost = System.getModule("com.wellsfargo.v2.ad.api").getAdHostByName(
        inputProperties.customProperties.ad_domain
    );

    System.getModule("com.wellsfargo.v2.ad.api").changeComputerOU(
        inputProperties.resourceNames[0],
        inputProperties.customProperties.ad_OU,
        adHost
    );
}

function sendFailureNotification(inputProperties, exception) {
    var addresses = System.getModule("com.tungsten.email.api.configuration").getEnvironmentAddressConfiguration(System.getModule("com.tungsten.system.configuration").getConfigurationProfile("emailaddresses"));

    var table = new Properties(
        {
            "Deployment ID": inputProperties.deploymentId,
            "VM Name": inputProperties.resourceNames[0],
            "Exception": exception
        }
    );


System.getModule("com.tungsten.email.api").sendEmailFromTemplate(System.getModule("com.tungsten.system.configuration").getConfigurationProfile("emailhost"), "email", "standard table", new Properties({table_title: "Request Details", title: "Change OU for vm '" + inputProperties.resourceNames[0] + "' failed.", subject: "Change OU for vm '" + inputProperties.resourceNames[0] + "' failed.", description: "Change OU for vm '" + inputProperties.resourceNames[0] + "' failed.", table_header: "Request Details", table: table, vraURL: System.getModule("com.tungsten.vra.api").getVRAUrl(System.getModule("com.tungsten.system.configuration").getConfigurationProfile("vra"))}), addresses.fromName, addresses.fromAddress, addresses.administratorAddress);

}
