workflow-item.script:
"use strict"

try {
    changeOU(inputProperties);
} catch(exception) {
    System.error("Unable to change the OU for VM '" + inputProperties.resourceNames[0] + "': " + exception);
}

/**
 * 
 */ 
function changeOU(inputProperties) { 
    var adHost = System.getModule("com.wellsfargo.v2.ad.api").getAdHostByName(
        inputProperties.customProperties.ad_domain
    );

    System.getModule("com.wellsfargo.v2.ad.api").changeComputerOU(
        inputProperties.resourceNames[0],
        "OU=Staging,OU=OS_Build,OU=SVR,OU=ENT,DC=ent,DC=wfb,DC=bank,DC=qa",
        adHost
    );
}
