workflow-item.script:
var retries = 0;
var sleep = 5000; //5 seconds
var allowedRetries = 60; //5 minutes

System.log("Waiting for VM to shutdown....");
while(!vm.guest.guestOperationsReady) { 
    System.log("Guest tools is still available sleeping 5 seconds....");
    System.sleep(sleep);
    retries++;
    if(retries > allowedRetries) {
        System.warn("Never detected the OS rebooting, continuing....")
        break;
    }
}
System.log("VM shutdown....");


retries = 0;
sleep = 10000; //10 seconds
allowedRetries = 60; //10 minutes
System.log("Waiting for VM to be available....");
while(!vm.guest.guestOperationsReady) { 
    System.log("Guest tools is not available sleeping 5 seconds....");
    System.sleep(sleep);
    retries++;
    if(retries > allowedRetries) {
        throw "Never detected the OS booted!";
    }    
}

System.log("VM Tools is avilable, waiting one more minute...");
System.sleep(60000);
System.log("Continuing...");
